import {useState,useEffect} from 'react';
import fire from '../config/fire';
import History from './History';



export default () => {

  const [products,setProducts] = useState([])
  const [filteredProducts,setfilteredProducts] = useState([])
  const [cartItems,setCartItems] = useState([])
  const [search, setSearch] = useState("");
  const [usrLogin,setUsr_Login] = useState("");
  const [pwdLogin,setPwd_Login] = useState("");
  const [cnfPwdLogin,setCnfPwd_Login] = useState("");
  const [user,setUser] = useState({});

  const db = fire.firestore();


    //Products
    useEffect(() => {
        db.collection('products')
        .get()
        .then(response => {
            const data = response.docs.map(doc => {
                const productData = doc.data();
                productData.id=doc.id;
                return productData
            });
                setProducts(data)
                setfilteredProducts(data)
            })
         return () => {

        }
    }, []);

    console.log(products,filteredProducts)

 //Local Storage
  useEffect(() => {
       let cartItem =  db.collection('User/').doc(user.userId).cartItems
        if(cartItem){
            setCartItems(cartItem)
        }
        return () => {
          localStorage.getItem("cartItems");
        };
    }, [])


  //Search 
  useEffect(() => {
      setfilteredProducts(
            [...products].filter(product => {
                return product.title.toLowerCase().includes(search.toLowerCase());
            })
      )
    return () => {
      console.log("clean up");
    };
  }, [search,products])

  useEffect(() => {
    fire.auth().onAuthStateChanged((user)=>{
        if(user)
        {
          setUser({userId:user.uid})
        }
        else{
          setUser({
                   userId : null,
                   cartItems:[],
                   previousOrders:{}
            })
        }
      })
      return () => {

      }
  }, [])
 console.log(user,"asd")




return{
    filteredProducts:filteredProducts,
    cartItems:cartItems,
    search:search,


    onAddCart : async(product) => {
        if(user.userId!==null){
            const cartItem = cartItems;
            let productAlreadyInCart = false;
            cartItem.forEach(item => {
                if(item.id===product.id){
                    productAlreadyInCart  = true;
                    item.count++;
                }
            })
            if(!productAlreadyInCart){
                cartItem.push({...product,count:1})
            }
            setCartItems([...cartItem])
            localStorage.setItem("cartItems",JSON.stringify(cartItems));
            await db.collection('User/').doc(user.userId).set({cartItems:cartItem})
      }
    },
      handleSearch : (value) => {
        setSearch(value);
      },

      handleChangeSort : (sort) => {
        if(sort!==''){
            const updated =  [...products].sort((a,b) => {
              return (sort==="lowest")?(a.price > b.price?1:-1):(a.price < b.price?1:-1);
            });
            setfilteredProducts(updated);
        }
        else{
          const updated = [...products].sort((a,b)=> { return (a.id < b.id ?1:-1)});
          setfilteredProducts(updated);
        }
      },

      onRemoveCart : async (removeItem) => {
          if(user.userId!==null){
            const update =  [...cartItems].filter(item => {
                    if(item.id===removeItem.id && removeItem.count<=1)
                    return false;
                    else{
                    if(item.id===removeItem.id && removeItem.count>1)
                        item.count--;
                    return true;
                    }
                });
                setCartItems(update);
                localStorage.setItem("cartItems",JSON.stringify(cartItems));
                await db.collection('User/').doc(user.userId).update({cartItems:update})
            }
      },

      userLoginChange:(value) => {
          setUsr_Login(value);
      },

      pwdLoginChange:(value) =>{
          setPwd_Login(value)
      },

      cnfPwdLoginChange:(value) => {
          setCnfPwd_Login(value)
      },

      login:(e)=>{
          e.preventDefault();
          fire.auth().signInWithEmailAndPassword(usrLogin,pwdLogin).then((res)=>{
              if(res){
                //   setUser({...user,login:true});
                  History.push('/products');
              }
          })
          .catch((err)=>{
            // setUser({...user,login:false});

            console.log(err)
          })
      },

      signUp:(e)=>{
            e.preventDefault();
            if(pwdLogin === cnfPwdLogin){
                fire.auth().createUserWithEmailAndPassword(usrLogin,pwdLogin).then((res)=>{
                if(res){
                    // setUser({...user,login:true});
                    History.push('/products')
                }
                })
                .catch((err)=>{
                    // setUser({...user,login:false});
                    console.log(err)
                })
            }
            else{
                alert("pwd didnt match")
            }
        },
        logout:() => {
            fire.auth().signOut();
        },
        user:user

}

}
