import React,{useState,useEffect} from 'react';
import '../css/App.css';
import Header from './Header';
import Footer from './Footer';
import Signin from './Signin';
import Signup from './Signup';
import Menu from './Menu';
import ProductCard from './ProductCard';
import Filter from './Filter';
import Baskets from './Baskets';
import { Provider } from 'react-redux';
import store from './store';



function App() {

  const [products,setProducts] = useState([]);
  const [filteredProducts, setfilteredProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [cartItems,setCartItems] = useState([]);

//Products


  //Local Storage
  useEffect(() => {
    if(localStorage.getItem("cartItems")){
      setCartItems(JSON.parse(localStorage.getItem("cartItems")));
    }
        return () => {
          localStorage.getItem("cartItems");
          console.log("clean up");
        };
    }, [])

  //Search 
  useEffect(() => {
    setfilteredProducts(
        [...products].filter(product => {
           return product.title.toLowerCase().includes(search.toLowerCase());
        })
    )
    return () => {
      console.log("clean up");
    };
  }, [search,products])

  const handleSearch = (value) => {
    setSearch(value);
  }

  const handleChangeSort = (sort) => {
      if(sort!==''){
          const updated =  [...products].sort((a,b) => {
            return (sort==="lowest")?(a.price > b.price?1:-1):(a.price < b.price?1:-1);
          });
         setfilteredProducts(updated);
      }
      else{
        const updated = [...products].sort((a,b)=> { return (a.id < b.id ?1:-1)});
        setfilteredProducts(updated);
      }
  } 

  const onAddCart = (product) => {
    const cartItem = cartItems;
    let productAlreadyInCart = false;
    cartItem.forEach(item => {
        if(item.id===product.id){
            productAlreadyInCart  = true;
            item.count++;
        }
    })
      if(!productAlreadyInCart){
        cartItem.push({...product,count:1})
      }
    setCartItems([...cartItem]);
    localStorage.setItem("cartItems",JSON.stringify(cartItems));
  }

  const onRemoveCart = (removeItem) => {
    console.log(removeItem,"asd")
    setCartItems([...cartItems].filter(item => {
        if(item.id===removeItem.id && removeItem.count<=1)
          return false;
        else{
          if(item.id===removeItem.id && removeItem.count>1)
            item.count--;
          return true;
        }
    }));
    localStorage.setItem("cartItems",JSON.stringify(cartItems));
  }


  return (
    <Provider store={store}>
    <div>
      <Header title="AGRI-Cart"/>
      {/* <Signup />
      <Signin/> */}
      <Menu/>
      <div className="container">
          <div className="row">
            <div className="col-md-8">
             <Filter count={filteredProducts.length} handleChangeSort={handleChangeSort} handleSearch = {handleSearch} search={search} />
              <ProductCard products={filteredProducts} onAddCart={onAddCart}/>
            </div>
          <div className="col-md-4 sticky basket">
              <Baskets cartItems={cartItems} onRemoveCart={onRemoveCart} onAddCart = {onAddCart}/>
        </div>
        </div>
      </div>
      <Footer/>
    </div>
    </Provider>
  );
}

export default App;
