import React,{useState,useEffect} from 'react';
import '../css/App.css';
import Header from './Header';
import Footer from './Footer';
import Signin from './Signin';
import Signup from './Signup';
import Menu from './Menu';
import ProductCard from './ProductCard';
import Filter from './Filter';
import Baskets from './Baskets';
import Story from './Story';


function App() {

  const [products,setProducts] = useState([]);
  const [filteredProducts, setfilteredProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [cartItems,setCartItems] = useState([]);


  useEffect(() => {
    fetch("http://localhost:8000/products/")
    .then(res => res.json())
    .then(jsonResponse => { 
      if(jsonResponse){
        setProducts(jsonResponse);
        setfilteredProducts(jsonResponse);
      }
    })
  }, [products]);

  useEffect(() => {

    return () => {
      if(localStorage.getItem("cartItems")){
        setCartItems(JSON.parse(localStorage.getItem("cartItems")));
       }
    };
  }, [])
  //Search 
  useEffect(() => {
    setfilteredProducts(
        [...products].filter(product => {
           return product.title.toLowerCase().includes(search.toLowerCase());
        })
    )
  }, [search,products])

  const handleSearch = (value) => {
    setSearch(value);
  }

  const handleChangeSort = (sort) => {
      if(sort!==''){
          const updated =  [...products].sort((a,b) => {
            return (sort==="lowest")?(a.price > b.price?1:-1):(a.price < b.price?1:-1);
          });
         setfilteredProducts(updated);
      }
      else{
        const updated = [...products].sort((a,b)=> { return (a.id < b.id ?1:-1)});
        setfilteredProducts(updated);
      }
  } 

  const onAddCart = (product) => {
    const cartItem = cartItems;
    let productAlreadyInCart = false;
    cartItem.forEach(item => {
        if(item.id===product.id){
            productAlreadyInCart  = true;
            item.count++;
        }
    })
      if(!productAlreadyInCart){
        cartItem.push({...product,count:1})
      }
    setCartItems([...cartItem]);
    localStorage.setItem("cartItems",JSON.stringify(cartItems));
  }

  const onRemoveCart = (removeItem) => {
    setCartItems([...cartItems].filter(item => item.id!==removeItem.id));
    localStorage.setItem("cartItems",cartItems);
  }

   const [subtotal,setSubtotal] = useState(0);

const calculateSubtotal = () => {
    setSubtotal(0);
    cartItems.map(item => {
        setSubtotal( subtotal + (item.price * item.count));
    })
}

  return (
    <div>
      <Header title="AGRI-Cart"/>
      {/* <Signup />
      <Signin/> */}
      {/* <Menu/>
      <div className="container">
          <div className="row">
            <div className="col-md-8">
             <Filter count={filteredProducts.length} handleChangeSort={handleChangeSort} handleSearch = {handleSearch} search={search} />
              <ProductCard products={filteredProducts} onAddCart={onAddCart}/>
            </div>
          <div className="col-md-4 sticky">
              <Baskets cartItems={cartItems} onRemoveCart={onRemoveCart} subtotal={subtotal}/>
        </div>
        </div>
      </div> */}
      <Story/>
      <Footer/>
    </div>
  );
}

export default App;
