 import React from 'react';
 import utils from '../utils';
import useProducts from './useProducts';


 function ProductCard(props) {

    const {filteredProducts,onAddCart} = useProducts();


     return (
        <div className="row product">
                            {
                            filteredProducts.map(product => (
                            <div className="col-md-4 car" key = {product.id}>
                                <figure className="card card-product">
                                    <div className="img-wrap"><img src={`../products/${product.src}`} alt={product.title}/></div>
                                    <figcaption className="info-wrap">
                                            <h4 className="title">{product.title}</h4>
                                            <p className="desc">{product.description}</p>
                                    </figcaption>
                                    <div className="bottom-wrap">
                                        <button onClick={()=> onAddCart(product)} className="cart btn pull-right">Add to Cart</button>
                                        <div className="price-wrap h5">
                                            <span className="price-new">{utils.formatCurrency(product.price)}</span> 
                                        </div> 
                                    </div> 
                                </figure>
                            </div> 
                            ))
                            }
                        </div>
     )
 }

 export default ProductCard;
