import { FETCH_PRODUCTS } from "./types"

export const fetchProducts = () => {

    fetch("http://localhost:8000/products/")
    .then(res => res.json())
    .then(jsonResponse => { 
      return {type:FETCH_PRODUCTS,payload:jsonResponse};
    })

}
