import React, { useReducer } from 'react';
import '../css/baskets.css';
import utils from '../utils'
import cartReducer from '../reducer/cartReducer';
import { FETCH_CART_ITEMS, REMOVE_FROM_CART, ADD_TO_CART } from '../actions/types';

const Baskets = (props) => {

    console.log(props,"Asd");
    const initialState = {
        cartItems:[]
    }

    const [state,dispatch] = useReducer(cartReducer,initialState)
    useEffect(() => {
        if(localStorage.getItem("cartItems")){
            dispatch({type:FETCH_CART_ITEMS,data:JSON.parse(localStorage.getItem("cartItems"))})
        }
            return () => {
              localStorage.getItem("cartItems");
              console.log("clean up");
            };
        }, [])

    return(
        <div className="Cart-Wrapper">
         <div className="Cart-Heading">Cart</div>
            <div className="Cart-Items">{state.cartItems.length} Items</div>
            {state.cartItems.length>0 ?
            <div>
                <div className="table">
                {state.cartItems.map(item => (
                    <div className="layout-inline row" key={item.id}>
                <div className="col col-pro layout-inline">
                    <p>{item.title}</p>
                </div>

                <div className="col col-qty layout-inline">
                <div className="quantity buttons_added">
                    <input type="button" value="-" onClick={()=>dispatch({type:REMOVE_FROM_CART, data:item})} className="minus"/>
                    <input type="number"  name="quantity" value={item.count} title="Qty" className="input-text qty text" size="4" readOnly/>
                    <input type="button" value="+" onClick={()=>dispatch({type:ADD_TO_CART, data:item})} className="plus"/>
                </div>
                </div>


                <div className="col col-total col-numeric">               
                    <p> {utils.formatCurrency(item.price * item.count)}</p>
                </div>
                </div>

                ))
                }
                </div>
                    <div className="subTotal">
                        Subtotal <span className="total-charges">
                        {utils.formatCurrency(state.cartItems.reduce((a,c) => a + c.price * c.count,0))}
                    </span>
                    </div>
                    <p className="extra">Extra charges may apply</p>
                    <div className="checkout">
                        <button type="submit" value="Checkout" className="check">Checkout →</button>
                    </div>
                    </div>:''}

      </div>
    )






}

export default Baskets;
