import {FETCH_PRODUCTS, SEARCH_PRODUCTS, SORT_PRODUCTS, FETCH_CART_ITEMS, ADD_TO_CART}  from "../actions/types";


const productReducer = (state , action) => {
	switch (action.type) {
		case FETCH_PRODUCTS:
			return {...state,
                products: action.payload,
                filteredProducts:action.payload
            }
         case SEARCH_PRODUCTS:
                return {
                    ...state,
                    filteredProducts:[...state.products].filter(product => {
                        return product.title.toLowerCase().includes(action.data.toLowerCase());
                     })
                    }
         case SORT_PRODUCTS:
                if(action.data!==''){
                    const updated =  [...state.products].sort((a,b) => {
                      return (action.data==="lowest")?(a.price > b.price?1:-1):(a.price < b.price?1:-1);
                    });
                   return {...state,filteredProducts:updated};
                }
                else{
                  const updated = [...state.products].sort((a,b)=> { return (a.id < b.id ?1:-1)});
                  return {...state,filteredProducts:updated};
                }
        case FETCH_CART_ITEMS:
                return{
                    ...state,
                    cartItems:action.data
                }
        case ADD_TO_CART:
                const cartItem = [];
                let productAlreadyInCart = false;
                cartItem.forEach(item => {
                    if(item.id===action.data.id){
                        productAlreadyInCart  = true;
                        item.count++;
                    }
                })
                  if(!productAlreadyInCart){
                    cartItem.push({...action.data,count:1})
                  }
                  console.log(action.data,cartItem,state.cartItems,"cart");
                  return{
                      ...state,
                      cartItems:cartItem
                  }

		default:
			return state
	}
}
export default  productReducer;
