import React from 'react';
import '../css/productBox.css';
import utils from '../utils';
import Filter from './Filter';
import Baskets from './Baskets';


const ProductCard = (props) => {

    return (
        <div className="container">
             <div className="row">
                  <Menu/>
                    <div className="col-md-8">
                        <Filter count={props.products.length} handleChangeSort={props.handleChangeSort} handleSearch = {props.handleSearch} search={props.search} />
                        <div className="row product">
                            {
                            props.products.map(product => (
                            <div className="col-md-4 car" key = {product.id}>
                                <figure className="card card-product">
                                    <div className="img-wrap"><img src={`../products/${product.src}`} alt={product.title}/></div>
                                    <figcaption className="info-wrap">
                                            <h4 className="title">{product.title}</h4>
                                            <p className="desc">{product.description}</p>
                                    </figcaption>
                                    <div className="bottom-wrap">
                                        <button onClick={()=> props.onAddCart(product)} className="cart btn pull-right">Add to Cart</button>
                                        <div className="price-wrap h5">
                                            <span className="price-new">{utils.formatCurrency(product.price)}</span> 
                                        </div> 
                                    </div> 
                                </figure>
                            </div> 
                            ))
                            }
                        </div>
                   </div>
                   <div className="col-md-4 sticky basket">
                      <Baskets cartItems={props.cartItems} onRemoveCart={props.onRemoveCart} onAddCart = {props.onAddCart}/>
                   </div>
            </div>
        </div>



    )
}




export default ProductCard;
