import {FETCH_PRODUCTS, SEARCH_PRODUCTS}  from "../actions/types";

const productReducer = (state, action) => {
	switch (action.type) {
		case FETCH_PRODUCTS:
			return {
                products: action.payload,
                search:""
            }
         case SEARCH_PRODUCTS:
                return {
                        products: action.payload,
                        search: action.data,
                    }
		default:
			return state
	}
}
export default  productReducer;
