import React from 'react';
import './productBox.css';
import utils from '../utils'


const ProductCard = (props) => {


    return (
        <div class="row">
{
props.products.map(product => (
<div class="col-md-4">
	<figure class="card card-product">
		<div class="img-wrap"><img src={`../products/${product.src}`} alt={product.title}/></div>
		<figcaption class="info-wrap">
				<h4 class="title">{product.title}</h4>
				<p class="desc">{product.description}</p>
				<div class="rating-wrap">
					<div class="label-rating">132 reviews</div>
					<div class="label-rating">154 orders </div>
				</div> 
		</figcaption>
		<div class="bottom-wrap">
            <button onClick={()=> props.onAddCart(product)} class="btn btn-sm btn-primary pull-right">Add to Cart</button>
			<div class="price-wrap h5">
				<span class="price-new">{utils.formatCurrency(product.price)}</span> 
			</div> 
		</div> 
	</figure>
</div> 
))
}
</div>
