import React,{useState,useEffect} from 'react';
import '../css/App.css';
import Header from './Header';
import Footer from './Footer';
import Signin from './Signin';
import Signup from './Signup';
import Menu from './Menu';
import ProductCard from './ProductCard';
import Filter from './Filter';


function App() {

  const [products,setProducts] = useState([]);
  const [filteredProducts, setfilteredProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/products/")
    .then(res => res.json())
    .then(jsonResponse => { 
      if(jsonResponse){
        setProducts(jsonResponse);
        setfilteredProducts(jsonResponse);
      }
    })
  }, []);

  const handleChangeSort = (sort) => {
      if(sort!=''){
        products.sort((a,b) => (sort=="lowest")?(a.price > b.price?1:-1):(a.price < b.price?1:-1) );
        setfilteredProducts(products)
      }
      else{
        products.sort((a,b)=> (a.id < b.id ?1:-1));
        setfilteredProducts(products)
      }
  } 

  const onAddCart = (value) => {
    console.log(value,"Asd");
  }


  return (
    <div className="App">
      <Header title="AGRI-Cart"/>
      {/* <Signup />
      <Signin/> */}
      <Menu/>
      <div className="container">
          <div className="row">
            <div className="col-md-8">
             <Filter count={filteredProducts.length} handleChangeSort={handleChangeSort} />
             {
               filteredProducts.map((product) => {
                     <ProductCard id={product.id} src={product.src} title={product.title} price={product.price} description={product.description} onAddCart={onAddCart}/>

               })
             }
            </div>
          <div className="col-md-4">

        </div>
        </div>
      </div>
      <Footer/>
    </div>
  );
}

export default App;
