import {FETCH_PRODUCTS}  from "../actions/types";

const productReducer = (state, action) => {
	switch (action.type) {
		case 'FETCH_PRODUCTS':
			return {
				products: action.payload,
			}
		case 'FETCH_ERROR':
			return {
				products: {},
			}
		default:
			return state
	}
}
export default  productReducer;
