import React, { Fragment } from "react";
import "../css/App.css";
import Header from "./Header";
import Footer from "./Footer";
import Signin from "./Signin";
import Signup from "./Signup";
import Products from "./Products";
import { Router, Route, Switch } from "react-router-dom";
import useProducts from "./useProducts";
import Help from "./Help";
import Offers from "./Offers";
import Homepage from "./Homepage";
import History from "./History";
import Checkout from "./Checkout";
import OrderConfirmation from "./OrderConfirmation";
import Orders from "./Orders";

function App() {
  const {
    filteredProducts,
    cartItems,
    onAddCart,
    onRemoveCart,
    handleChangeSort,
    handleSearch,
    search,
    userLoginChange,
    pwdLoginChange,
    login,
    signUp,
    cnfPwdLoginChange,
    user,
    logout,
    onCheckout,
    orders,
    buy,
  } = Products();

  return (
    <Fragment>
      <Router history={History}>
        <Header
          title="AGRI-Cart"
          count={cartItems}
          user={user}
          logout={logout}
          buy={buy}
        />
        <Switch>
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs"
            exact
            render={() => <Homepage buy={buy} />}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/signup"
            render={() => (
              <Signup
                userLoginChange={userLoginChange}
                pwdLoginChange={pwdLoginChange}
                cnfPwdLoginChange={cnfPwdLoginChange}
                signUp={signUp}
                buy={buy}
              />
            )}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/signin"
            render={() => (
              <Signin
                userLoginChange={userLoginChange}
                pwdLoginChange={pwdLoginChange}
                login={login}
                buy={buy}
              />
            )}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/products"
            render={() => (
              <Products
                products={filteredProducts}
                cartItems={cartItems}
                onAddCart={onAddCart}
                onRemoveCart={onRemoveCart}
                handleChangeSort={handleChangeSort}
                handleSearch={handleSearch}
                search={search}
                onCheckout={onCheckout}
              />
            )}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/help"
            render={() => <Help />}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/offers"
            render={() => <Offers />}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/checkout"
            render={() => (
              <Checkout
                cartItems={cartItems}
                onAddCart={onAddCart}
                onRemoveCart={onRemoveCart}
              />
            )}
          />
          <Route
            path="/Agricart-online-vegetable-shopping-reactjs/orderConfirmation"
            render={() => <OrderConfirmation />}
          />
          <Route
            Path="/Agricart-online-vegetable-shopping-reactjs/orders"
            render={() => <Orders orders={orders} />}
          />
        </Switch>

        <Footer />
      </Router>
    </Fragment>
  );
}

export default App;
