import React from 'react';

const Baskets = (props) => {

    return(
            <div className="alert alert-info">
                {props.cartItems.length===0 ? "Basket is Empty": <div>you have {props.cartItems.length} products in Basket </div>}

                {props.cartItems.length>0 ?
                    <div>
                        <ul>
                            {props.cartItems.map(item => (
                                <li>
                                    {item.title}
                                    <button className="btn btn-danger" onClick={() => props.onRemoveCart(item)}>X</button>

                                </li>

                            ))
                            }

                        </ul>
                    </div>:''
                }
            </div>

    )



}

export default Baskets;
