import React from 'react';
import '../css/baskets.css';

const Baskets = (props) => {

    return(

        <div className="Cart-Wrapper">
             <div className="Cart-Heading">Cart</div>
             <div className="Cart-Items">{props.cartItems.length===0 ? "Basket is Empty": <div>{props.cartItems.length} Items in Basket </div>}</div>

            {props.cartItems.length>0 ?

                <ul className="cart-list">
                    {props.cartItems.map(item => (
                    <li className="cart-list-item">
                        <div className="cart-item">
                            <div className="cart-item-description">
                                <span>
                                    <p className="cart-item-label">{item.title}</p>
                                    <p className="cart-item-price">{item.price}</p>
                                </span>
                            </div>
                            <div className="remove-cart">
                                   <button className="btn btn-danger" onClick={() => props.onRemoveCart(item)}>X</button>
                            </div>

                        </div>
                    </li>
                    ))}
                    </ul>:''}
             </div>
