import React from 'react';
import './productBox.css';
import utils from '../utils'


const ProductCard = (props) => {


    return (
        <div className="row">
        {
            props.products.map(product => (
                <div className={props.name} key={product.id}>
                    <div className="card">
                        <img key={product.id} src={`../products/${product.src}`} alt={product.title}/>
                        <h2>{product.title}</h2>
                        <p className="description">{product.description}</p>
                        <p className="price">{utils.formatCurrency(product.price)}</p>
                        <p><button onClick={()=> props.onAddCart(product)}>Add to Cart</button></p>
                    </div>
            </div>
            ))
        }
        </div>
    )
}

export default ProductCard;
