import {useState,useEffect,useReducer} from 'react';
import productReducer from '../reducer/productReducer';
import { FETCH_PRODUCTS, SEARCH_PRODUCTS, SORT_PRODUCTS, FETCH_CART_ITEMS, PRODUCTS_REDIRECT} from '../actions/types';
import fire from '../config/fire';
import React from 'react';
import { Redirect } from 'react-router-dom';
import History from './History';



export default () => {


  const [search, setSearch] = useState("");
  const [usrLogin,setUsr_Login] = useState("");
  const [pwdLogin,setPwd_Login] = useState("");
  const [cnfPwdLogin,setCnfPwd_Login] = useState("");
  const [user,setUser] = useState({})


  const initialStateProducts = {
      products:[],
      filteredProducts:[],
      search:"",
      cartItems:[]
    }

  const [state, dispatch] = useReducer(productReducer, initialStateProducts)

    //Products
    useEffect(() => {
      fetch("http://localhost:8000/products/")
        .then(res => res.json())
        .then(jsonResponse => { 
            dispatch({type:FETCH_PRODUCTS,payload:jsonResponse});
        })
     return () => {

        }
    }, []);

 //Local Storage
  useEffect(() => {
    if(localStorage.getItem("cartItems")){
        dispatch({type:FETCH_CART_ITEMS,data:JSON.parse(localStorage.getItem("cartItems"))})
    }
        return () => {
          localStorage.getItem("cartItems");
          console.log("clean up");
        };
    }, [])


  //Search 
  useEffect(() => {
    dispatch({type:SEARCH_PRODUCTS,data:search})
    return () => {
      console.log("clean up");
    };
  }, [search,state.products])






return{
    filteredProducts:state.filteredProducts,
    cartItems:state.cartItems,
    onAddCart : (product) => {
        const cartItem = state.cartItems;
        let productAlreadyInCart = false;
        cartItem.forEach(item => {
            if(item.id===product.id){
                productAlreadyInCart  = true;
                item.count++;
            }
        })
          if(!productAlreadyInCart){
            cartItem.push({...product,count:1})
          }
        dispatch({type:FETCH_CART_ITEMS,data:cartItem});
        localStorage.setItem("cartItems",JSON.stringify(state.cartItems));
      },
      handleSearch : (value) => {
        setSearch(value);
      },
      handleChangeSort : (sort) => {
         dispatch({type:SORT_PRODUCTS,data:sort})
      },
      onRemoveCart : (removeItem) => {
       const update =  [...state.cartItems].filter(item => {
            if(item.id===removeItem.id && removeItem.count<=1)
              return false;
            else{
              if(item.id===removeItem.id && removeItem.count>1)
                item.count--;
              return true;
            }
        });
        dispatch({type:FETCH_CART_ITEMS ,data:update})
        localStorage.setItem("cartItems",JSON.stringify(state.cartItems));
      },
      search:search,
      userLoginChange:(value) => {
          setUsr_Login(value);
      },
      pwdLoginChange:(value) =>{
          setPwd_Login(value)
      },
      cnfPwdLoginChange:(value) => {
          setCnfPwd_Login(value)
      },
      login:(e)=>{
          e.preventDefault();
          fire.auth().signInWithEmailAndPassword(usrLogin,pwdLogin).then((res)=>{
              if(res){
                  History.push('/products')
              }
              console.log(res,"sdi")
          })
          .catch((err)=>{
            console.log(err)
          })
      },
      signUp:(e)=>{
        e.preventDefault();
        if(pwdLogin === cnfPwdLogin){
        fire.auth().createUserWithEmailAndPassword(usrLogin,pwdLogin).then((res)=>{
          if(res){
            dispatch({type:PRODUCTS_REDIRECT,data:true});
          }
        })
        .catch((err)=>{
          console.log(err)
        })
    }
    else{
        alert("pwd didnt match")
    }
    }

}

}
