import { FETCH_PRODUCTS } from "../actions/types";

const productReducer = (state,action) => {
    switch (action.type) {
        case FETCH_PRODUCTS:
                return {...state,items:action.payload}
        default:
                return state;
    }
}
export default  productReducer;
