import React,{Fragment} from 'react';
import '../css/App.css';
import Header from './Header';
import Footer from './Footer';
import Signin from './Signin';
import Signup from './Signup';
import Products from './Products';
import {Router, Route, Switch} from 'react-router-dom';
import useProducts from './useProducts';
import Help from './Help';
import Offers from './Offers';
import Homepage from './Homepage';
import History from './History'

function App() {

  const {filteredProducts, cartItems, onAddCart, onRemoveCart, handleChangeSort, handleSearch, search,userLoginChange,pwdLoginChange,login,signUp,cnfPwdLoginChange,user} = useProducts();

  return (
    <Fragment>
      <Router history = {History} >
      <Header title="AGRI-Cart" count={cartItems.length} user={user}/>
      <Switch>
        <Route path="/" exact render={()=> < Homepage/>} />
        <Route path="/signup" render={()=> < Signup userLoginChange={userLoginChange} pwdLoginChange={pwdLoginChange} cnfPwdLoginChange={cnfPwdLoginChange} signUp = {signUp}/>} />
        <Route path="/signin" render={()=> <Signin userLoginChange={userLoginChange} pwdLoginChange={pwdLoginChange} login={login}/>} />
        <Route path="/products" render={()=><Products products={filteredProducts} cartItems={cartItems} onAddCart={onAddCart} onRemoveCart={onRemoveCart} handleChangeSort={handleChangeSort} handleSearch={handleSearch} search={search}/>} />
        <Route path="/help" render={()=> <Help/>} />
        <Route path="/offers" render={()=> <Offers/>} />

      </Switch>

      <Footer/>
      </Router>
    </Fragment>
  );
}

export default App;
