import {useState,useEffect,useReducer} from 'react';
import { FETCH_PRODUCTS, SEARCH_PRODUCTS, SORT_PRODUCTS, FETCH_CART_ITEMS} from '../actions/types';


export default () => {


  const [search, setSearch] = useState("");

  const initialStateProducts = {
      products:[],
      filteredProducts:[],
      search:"",
      cartItems:[]
    }

  const [state, dispatch] = useReducer(productReducer, initialStateProducts)

//Products
    useEffect(() => {
      fetch("http://localhost:8000/products/")
        .then(res => res.json())
        .then(jsonResponse => { 
            dispatch({type:FETCH_PRODUCTS,payload:jsonResponse});
        })
          return () => {

            }
        }, []);

        console.log(state,"prod")

        //Local Storage
    useEffect(() => {
        if(localStorage.getItem("cartItems")){
            dispatch({type:FETCH_CART_ITEMS,data:JSON.parse(localStorage.getItem("cartItems"))})
        }
            return () => {
            localStorage.getItem("cartItems");
            console.log("clean up");
            };
        }, [])


  //Search 
  useEffect(() => {
    dispatch({type:SEARCH_PRODUCTS,data:search})

    return () => {
      console.log("clean up");
    };
  }, [search,state.products])



return{
    filteredProducts:state.filteredProducts,
    cartItems:state.cartItems,
    onAddCart : (product) => {
        const cartItem = state.cartItems;
        let productAlreadyInCart = false;
        cartItem.forEach(item => {
            if(item.id===product.id){
                productAlreadyInCart  = true;
                item.count++;
            }
        })
          if(!productAlreadyInCart){
            cartItem.push({...product,count:1})
          }
        dispatch({type:FETCH_CART_ITEMS,data:cartItem});
        localStorage.setItem("cartItems",JSON.stringify(state.cartItems));
      },
      handleSearch : (value) => {
        setSearch(value);
      },
      handleChangeSort : (sort) => {
         dispatch({type:SORT_PRODUCTS,data:sort})
      },
      onRemoveCart : (removeItem) => {
       const update =  [...state.cartItems].filter(item => {
            if(item.id===removeItem.id && removeItem.count<=1)
              return false;
            else{
              if(item.id===removeItem.id && removeItem.count>1)
                item.count--;
              return true;
            }
        });
        dispatch({type:FETCH_CART_ITEMS ,data:update})
        localStorage.setItem("cartItems",JSON.stringify(state.cartItems));
      },
      search

}

}
