import React from 'react';
import './productBox.css';


const ProductCard = (props) => {


    return (
        <div className={props.name}>
        {
            props.products.map(product => (
            <div className="card">
                <img src={'/products/${product.src}'} alt={product.title}/>
                <h1>{product.title}</h1>
                <p className="price">{product.price}</p>

                <div className="input-group center">
                    <span className="input-group-btn">
                        <button type="button" className="btn btn-danger btn-number"  data-type="minus">
                            <span className="glyphicon glyphicon-minus"></span>
                        </button>
                    </span>
                        <input type="text" name="break" className="form-control input-number" value="" min="0" max="60" readOnly/>
                    <span className="input-group-btn">
                        <button type="button" className="btn btn-success btn-number" data-type="plus">
                            <span className="glyphicon glyphicon-plus"></span>
                        </button>
                    </span>
                </div>
            </div>
            ))
        }
        </div>
    )
}

export default ProductCard;
