import React,{useState,useEffect, Fragment} from 'react';
import '../css/App.css';
import Header from './Header';
import Footer from './Footer';
import Signin from './Signin';
import Signup from './Signup';
import Products from './Products';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import useProducts from './useProducts';


function App() {

  const {filteredProducts,onAddCart,handleSearch,handleChangeSort,onRemoveCart,cartItems,search} = useProducts();

  return (
    <Fragment>
      <BrowserRouter>
      <Header title="AGRI-Cart" count="0"/>
      <Switch>
        <Route path="/signup" render={()=> < Signup/>} />
        <Route path="/signin" render={()=> <Signin/>} />
        <Route path="/products" render={()=> <Products  products={filteredProducts} onAddCart={onAddCart} handleChangeSort = {handleChangeSort} handleSearch = {handleSearch} search = {search} cartItems={cartItems} onRemoveCart = {onRemoveCart} />} />
      </Switch>

      <Footer/>
      </BrowserRouter>
    </Fragment>
  );
}

export default App;
